<style lang="scss">
.trace-line {
  position: absolute;
//   top: calc(1 * var(--data-top));
  left: 10;
  z-index: 0;
  transition: height;
  transition-timing-function: linear;
  transition-duration: calc(0.5ms * var(--data-height));
}
.trace-line-wrapper {
  overflow-x: hidden;
}
</style>
<script>
export default {
  name: "LineFollower",
  data() {
    return {
      style: {},
      height: 0,
      max_height: 0,
      width: "",
    };
  },
  mounted() {
    this.width = screen.width + 20;
    this.style = {
      "--data-top": screen.height,
      "--data-height": 0,
    };

    // // for dynamiceline height
    window.addEventListener("scroll", this.onScroll);
  },
  methods: {
    onScroll(e) {
      this.max_height = this.max_height
        ? this.max_height
        : document.getElementsByClassName("v-main")[0].scrollHeight - 200;

      console.log(this.max_height, this.height, window.top.scrollY);
      let tem_height = 0;
      if (this.max_height >= this.height || !this.height) {
        if (window.top.scrollY < 10) {
          tem_height = 0;
        } else if (
          window.top.scrollY + screen.height >
          document.body.scrollHeight
        ) {
          tem_height = document.body.scrollHeight;
        } else {
          tem_height = window.top.scrollY + screen.height / 1.2;
        }
        this.height =
          tem_height > this.max_height ? this.max_height : tem_height;
      }
      this.style = {
        "--data-top": screen.height,
        "--data-height": this.height - window.top.scrollY + screen.height,
      };
    },
  },
};
</script>
